import logo from '../logo.svg';
import React, { useEffect, useState } from "react";
import { Route, Switch } from "react-router-dom";
import NavBar from "./NavBar";
import '../App.css';
import Students from './Students';
import Home from './Home';
import Houses from './Houses';


import Gryffindor from './Gryffindor';
import Ravenclaw from './Ravenclaw';
import Hufflepuff from './Hufflepuff';
import Slytherin from './Slytherin';


function App() {

  const [students, setStudents] = useState([]);

  const [houses, setHouses] = useState([]);
  const [houseStudents, setHouseStudents] = useState([]);

  useEffect(() => {
    fetch("http://localhost:9292/students")
    .then((r) => r.json())
    .then((student) => setStudents(student))
  }, []);
  const student_id = students.map((student) => {
    return student.id
  })
  const student_name = students.map((student) => {
    return student.name
  })
  const student_year = students.map((student) => {
    return student.year_at_school
  })

 useEffect(()=>{
    fetch("http://localhost:9292/houses")
    .then((r)=>r.json())
    .then((house)=>setHouses(house))
 },[]);
 const house_name = houses.map((house) =>{
   return house.name
 })
 const house_id = houses.map((house)=> {
   return house.id
 })

  useEffect(() => {
    Promise.all([
      fetch("http://localhost:9292/houses/1"),
      fetch("http://localhost:9292/houses/2"),
      fetch("http://localhost:9292/houses/3"),
      fetch("http://localhost:9292/houses/4")
    ])
    .then(([res1, res2, res3, res4])=>{
      return Promise.all([res1.json(), res2.json(), res3.json(), res4.json()])
    })
    .then(([gryff, raven, huff, sly])=>setHouseStudents({gryff, raven, huff, sly}))
  },[]);
  console.log("(A)Promise all: ", houseStudents.sly)

  const house_student_id = houseStudents.raven.map((student) => {
    return student.id
  })
  const house_student_name = houseStudents.map((student) => {
    return student.name
  })

  console.log("(A)Student names: ", student_name)

  return (
    <div className="App">
      <NavBar />
        <Switch>
          <Route exact path='/'>
            <Home/>
          </Route>
          <Route path='/students'>
            <Students id={student_id} name={student_name} year={student_year} />
          </Route>
          <Route exact path='/houses'>
            <Houses name={house_name} />
          </Route>
          <Route path='/houses/gryffindor'>
            <Gryffindor id={house_student_id} name={house_student_name} />
          </Route>
          <Route path='/ravenclaw'>
            <Ravenclaw id={house_student_id} name={house_student_name} />
          </Route>
          <Route path='/hufflepuff'>
            <Hufflepuff id={house_student_id} name={house_student_name} />
          </Route>
          <Route path='/slytherin'>
            <Slytherin id={house_student_id} name={house_student_name} />
          </Route>
        </Switch>
    </div>
  );
}

export default App;
