import React from "react";

function ListItem({student_name, onRemove}){

  const {id, name} = student_name
  const student_id = student_name.map((names) => {
    return names.id
  })

  console.log("(LI)S ID: ", student_id)
  function handleRemoveClick(){
    fetch(`http://localhost:9292/students/${student_id}`, {
      method: "DELETE",
    });
    onRemove(student_id);
  }

  return(
    <div className = "listitem">
      {student_name.map((names)=>{
        return (
          <h2> 
            <li>
              {names.name} 
              {console.log("()",names.id)}
              <button 
                className="remove"
                onClick={handleRemoveClick}>
                  Remove
              </button> 
            </li>   
          </h2>
        )          
      })}
    </div>       
  )
}

export default ListItem;
