import React from "react";

function ListItem({student_name, id, s_year, onRemove}){

  console.log("(LI): ", student_name)
  console.log("(LI)ID: ", id)

  function handleRemoveClick(id){
    //console.log(e)
    fetch(`http://localhost:9292/students/${id}`, {
      method: "DELETE",
    });
    //onRemove(id);
  }

  return(
    <div className = "listitem">

        <div>
          {student_name.map((names)=>{
            return (
              <h2> 
                <li>
                  {names.name} 
                  {console.log("()",names.id)}
                  <button 
                    className="remove"
                    onClick={handleRemoveClick(names.id)}>
                      Remove
                  </button>   
                </li>
              </h2>
              )
          })}
        </div>

    </div>
  )
}

export default ListItem;
