import React, { useState, useEffect } from "react";
import ListItem from "./ListItem";
import NewStudent from './NewStudent';

function Students({names, handleRemove, handleAddNew}){
  let [students, setStudents] = useState([]);

  useEffect(()=>{
    fetch("http://localhost:9292/students")
    .then((r)=> r.json())
      .then((sly_list)=>setStudents(sly_list))
  },[]);
  const displayStu = students.filter((student)=>{
    return student.name
  })
  //console.log("(St) Students: ", students)

  const displayNames = names.filter((student)=>{
    return student.name
  })
  return(
    <div>
      <header>
        Currently Enrolled Students
      </header>
      <NewStudent onAddNewStudent={handleAddNew}/>
      {displayName.map((name) =>{
        return <ListItem 
          key={name.id}
          id={name.id}
          student_name={name.name}
          onRemove={handleRemove}
      />  })}



    </div>
  )
}

export default Students;
