import React from "react";
import ListItem from "./ListItem";
import NewStudent from './NewStudent';

function Students({names, id, handleRemove, handleAddNew}){

  const displayNames = names.filter((name)=>{
    return name.name
  })

  function handleRemoveClick(){
    fetch(`http://localhost:9292/students/${id}`, {
      method: "DELETE",
    });
   onRemove(id);
  }



  return(
    <div>
      <header>
        Currently Enrolled Students
      </header>
      <NewStudent onAddNewStudent={handleAddNew}/>
      {displayNames.map((names) =>{
        return <ListItem 
          key={names.id}
          id={names.id}
          student_name={names.name}
          //onRemove={handleRemove}
      /> 
    }, <button 
    className="remove"
    onClick={handleRemoveClick}>
      Remove
  </button> )}


    </div>
  )
}

export default Students;
