import React from "react";

function ListItem({student_name, id, s_year, onRemove}){

  function handleRemoveClick(){
    fetch(`http://localhost:9292/students/${id}`, {
      method: "DELETE",
    });
    onRemove(id)
  }


  return(
    <div className = "listitem">
      <li>
        <h2>{student_name}, ID:{id}
          <button 
            className="remove"
            onClick={handleRemoveClick}>Remove</button>
        </h2>
      </li>
    </div>
  )
}

export default ListItem;
